    ABB arbol = new ABB();
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        arbol.eliminar(Integer.parseInt(jTextField1.getText()));
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        arbol.insertar(Integer.parseInt(jTextField1.getText()));
        jTextField1.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        switch(jComboBox1.getSelectedItem().toString()){
            case "Inorden":
                System.out.println("Inorden:");
                jTextArea1.setText(arbol.getInOrden());
            break;    
            case "Preorden":
                System.out.println("PreOrden:");
                jTextArea1.setText(arbol.getPreOrden());
            break;  
            case "Postorden":
                System.out.println("PostOrden: ");
                jTextArea1.setText(arbol.getPostOrden());
            break;        
        }
        
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jTextField1KeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField1KeyPressed
        // TODO add your handling code here:
        char car = evt.getKeyChar();
        if( ( (car < '0') || (car > '9') ) && (car != '\b')){
            evt.consume();
            JOptionPane.showMessageDialog(null, car);
        }
    }//GEN-LAST:event_jTextField1KeyPressed

